import { b as AuroTabgroup, a as AuroTab, A as AuroTabpanel } from './auro-tabpanel.min.js';
import './lit-element.min.js';

AuroTabgroup.register();
AuroTab.register();
AuroTabpanel.register();

function onTabSelected(e) {
  console.info(`Tab selected: ${e.target.innerText}`);
}

function addListener() {
  window.addEventListener("tab-selected", onTabSelected);
}

function removeListener() {
  window.removeEventListener("tab-selected", onTabSelected);
}

function selectFirstTab() {
  const tabs = document.querySelector("#select-tab-example");
  tabs.selectTab(tabs.allTabs[0]);
}

function selectSecondTab() {
  const tabs = document.querySelector("#select-tab-example");
  const tabItems = tabs.allTabs;
  tabs.selectTab([...tabItems][1]);
}

function selectLastTab() {
  const tabs = document.querySelector("#select-tab-example");
  tabs.selectTab(tabs.allTabs[tabs.allTabs.length - 1]);
}

// Register the listener
addListener();

export { addListener, onTabSelected, removeListener, selectFirstTab, selectLastTab, selectSecondTab };
