import { i, x } from './lit-element.min.js';

class ExampleTabGroup extends i {
  /**
   *
   * @param {number} tabCount
   * @return {TemplateResult[]}
   */
  generateTabs(tabCount) {
    /** @type {TemplateResult[]} */
    const tabs = [];
    for (let i = 0; i <= tabCount; i++) {
      tabs.push(x`<auro-tab>Auto-generated ${i}</auro-tab>`);
    }

    return tabs;
  }

  /**
   *
   * @param {number} tabCount
   * @return {TemplateResult[]}
   */
  generatePanels(tabCount) {
    /** @type {TemplateResult[]} */
    const panels = [];
    for (let i = 0; i <= tabCount; i++) {
      panels.push(x`<auro-tabpanel>Auto-generated ${i}</auro-tabpanel>`);
    }

    return panels;
  }

  renderTabContent() {
    return x`
        <span>some content</span>
    `;
  }

  renderPanelContent() {
    return x`
        <span>some content</span>
    `;
  }

  render() {
    return x`
        <auro-tabgroup>
          <div slot="tabs">
            ${this.generateTabs(5)}
            <auro-tab>
              ${this.renderTabContent()}
            </auro-tab>
          </div>
          <div slot="panels">
            ${this.generatePanels(5)}
            <auro-tabpanel>
              ${this.renderPanelContent()}
            </auro-tabpanel>
          </div>
        </auro-tabgroup>
    `;
  }
}

function registerExampleGroup() {
  customElements.define("example-group-test", ExampleTabGroup);
}

export { ExampleTabGroup, registerExampleGroup };
